add_action( 'rest_api_init', 'get_form_data_into_custom_database' );
// API custom endpoints for WP-REST API
// http://localhost/wp-form/wp-json/home_retail/get/formdata
function get_form_data_into_custom_database() {
    register_rest_route('home_retail', '/get/formdata', array(
            'methods'  => 'POST',
            'callback' => 'add_formdata_get_all',
        )
    );
   }

   function add_formdata_get_all($request){
    $foo = (array)$_POST;
    $foo['bar'] = '1234';
    $foo = (object)$foo;
    $response = $foo ;
    return  $response; 
}

